SELECT CASE WHEN (SELECT h.women_age_15_years_and_above_with_high_141160_mgdl_blood_sug FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE r.area_name = 'Andhra Pradesh' AND t.name = 'Urban') > (SELECT h.women_age_15_years_and_above_with_high_141160_mgdl_blood_sug FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE r.area_name = 'Andhra Pradesh' AND t.name = 'Rural') THEN 'Urban' ELSE 'Rural' END;

