### SQL
SELECT CASE WHEN (SELECT h.men_age_15_years_and_above_wih_high_141160_mgdl_blood_sugar_ FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE r.area_name = 'Chhattisgarh' AND t.name = 'Urban') > (SELECT h.men_age_15_years_and_above_wih_high_141160_mgdl_blood_sugar_ FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE r.area_name = 'Chhattisgarh' AND t.name = 'Rural') THEN 'Urban' ELSE 'Rural' END;

