### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.children_under_age_3_years_breastfed_within_one_hour_of_birt AS DOUBLE PRECISION) ASC LIMIT 3;

