### SQL
SELECT CASE WHEN (SELECT h.children_under_age_3_years_breastfed_within_one_hour_of_birt FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE r.area_name = 'Puducherry' AND t.name = 'Urban') > (SELECT h.children_under_age_3_years_breastfed_within_one_hour_of_birt FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE r.area_name = 'Puducherry' AND t.name = 'Rural') THEN 'Urban' ELSE 'Rural' END;

