### SQL
SELECT CASE WHEN (SELECT h.pop_living_in_hh_with_electricity_ FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE r.area_name = 'Dadra & Nagar Haveli' AND t.name = 'Urban') > (SELECT h.pop_living_in_hh_with_electricity_ FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE r.area_name = 'Dadra & Nagar Haveli' AND t.name = 'Rural') THEN 'Urban' ELSE 'Rural' END;

