### SQL
SELECT h.mothers_who_had_at_least_4_antenatal_care_visits_for_last_bi FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE r.area_name = 'Chhattisgarh' AND t.name = 'Total';

