### SQL
SELECT e.main_agricultural_labourers_person FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE r.area_name = 'NCT of Delhi' AND t.name = 'Urban';

