### SQL
SELECT CASE WHEN (SELECT h.children_under_5_years_who_are_stunted_heightforage18_ FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE r.area_name = 'Mizoram' AND t.name = 'Urban') > (SELECT h.children_under_5_years_who_are_stunted_heightforage18_ FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE r.area_name = 'Mizoram' AND t.name = 'Rural') THEN 'Urban' ELSE 'Rural' END;

