### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.pop_living_in_hh_with_electricity_ AS DOUBLE PRECISION) ASC LIMIT 3;

