### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state = r.state JOIN tru t ON h.tru_id = t.id WHERE t.name = 'Total' ORDER BY CAST(h.institutional_births_in_the_5_years_before_the_survey_ AS DOUBLE PRECISION) DESC LIMIT 5;

