SELECT r.area_name FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' AND hs.obesity_p>hs.underweight_children;
SELECT r.area_name,(hs.elderly_with_illness*1.0/NULLIF(hs.elderly_population,0)) AS rate FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY rate ASC LIMIT 1;
SELECT r.area_name,ABS(hs.underweight_children-(hs.working_age_illness+hs.elderly_with_illness)) AS spread FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY spread ASC LIMIT 1;
SELECT r.area_name FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' AND (hs.diabetes_p+hs.hypertension_p+hs.asthma_p)>500000 AND hs.infant_mortality_rate<25;
SELECT r.area_name FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' AND hs.underweight_children<100000 AND hs.maternal_mortality_rate>40;
SELECT r.area_name,((hs.diabetes_p+hs.hypertension_p+hs.asthma_p+hs.cancer_p)*1.0/NULLIF(hs.tot_p,0)) AS loss FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY loss ASC LIMIT 1;
SELECT r.area_name FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' AND hs.vaccination_rate>90 AND hs.underweight_children>150000;
SELECT r.area_name,(hs.vaccination_rate/NULLIF(hs.infant_mortality_rate,0)) AS return_score FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY return_score DESC LIMIT 1;
SELECT r.area_name FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' AND (hs.diabetes_p+hs.hypertension_p)>400000 AND hs.underweight_children<80000;
SELECT r.area_name,ABS((hs.diabetes_male+hs.hypertension_male)-(hs.diabetes_female+hs.hypertension_female)) AS gap FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY gap ASC LIMIT 1;
SELECT r.area_name,ABS(SUM(CASE WHEN t.name='Urban' THEN hs.diabetes_p END)-SUM(CASE WHEN t.name='Rural' THEN hs.diabetes_p END)) AS delta FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id GROUP BY r.area_name ORDER BY delta ASC LIMIT 1;
SELECT r.area_name,(hs.unvaccinated_children+hs.underweight_children) AS preventable FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY preventable DESC LIMIT 1;
SELECT r.area_name FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' AND hs.vaccination_rate<75 AND hs.infant_mortality_rate<20;
SELECT r.area_name FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' AND hs.underweight_children>200000 AND hs.infant_mortality_rate<25;
SELECT r.area_name,((hs.elderly_with_illness-hs.working_age_illness)*1.0/NULLIF(hs.elderly_population,0)) AS ageing_cost FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY ageing_cost ASC LIMIT 1;
SELECT r.area_name,(hs.vaccination_rate-(hs.infant_mortality_rate+hs.maternal_mortality_rate)) AS score FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY score DESC LIMIT 1;
SELECT r.area_name,ABS(hs.underweight_children-hs.elderly_with_illness) AS inequality FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY inequality ASC LIMIT 1;
SELECT r.area_name FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' AND hs.underweight_children<70000 AND hs.elderly_with_illness>150000;
