SELECT r.area_name,(hs.vaccination_rate-(hs.diabetes_p+hs.hypertension_p)*1.0/NULLIF(hs.tot_p,0)) AS balance FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY balance DESC LIMIT 1;
SELECT r.area_name,(hs.underweight_children-(hs.diabetes_p+hs.hypertension_p)) AS improvement FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY improvement DESC LIMIT 1;
SELECT r.area_name FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY (hs.vaccination_rate-(hs.infant_mortality_rate+hs.maternal_mortality_rate)) DESC LIMIT 1;
