SELECT r.area_name,(hs.diabetes_p+hs.hypertension_p+hs.asthma_p+hs.cancer_p)*1.0/NULLIF(hs.tot_p,0) AS balance FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY balance ASC LIMIT 1;
SELECT r.area_name,ABS(hs.vaccination_rate-(100-hs.infant_mortality_rate)) AS link FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY link ASC LIMIT 1;
SELECT r.area_name,(hs.diabetes_p+hs.hypertension_p+hs.asthma_p+hs.cancer_p) FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' ORDER BY (hs.diabetes_p+hs.hypertension_p+hs.asthma_p+hs.cancer_p) ASC LIMIT 1;
