SELECT r.area_name FROM healthcare_stats hs JOIN regions r ON hs.state=r.state JOIN tru t ON hs.tru_id=t.id WHERE t.name='Total' AND hs.underweight_children<80000 AND hs.maternal_mortality_rate<20;
