### SQL
SELECT (SUM(e.literates_person) * 100.0 / NULLIF(SUM(e.total_person), 0)) FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE r.area_name = 'Uttar Pradesh' AND t.name = 'Total';

