### SQL
SELECT t.name, (SUM(e.literates_person) * 100.0 / NULLIF(SUM(e.total_person), 0)) AS literacy_rate FROM education_stats e JOIN tru t ON e.tru_id = t.id WHERE t.name IN ('Rural', 'Urban') GROUP BY t.name;

