### SQL
SELECT r.area_name, (SUM(e.literates_person) * 100.0 / NULLIF(SUM(e.total_person), 0)) AS literacy_rate FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY literacy_rate ASC LIMIT 1;

