### SQL
SELECT r.area_name, (SUM(e.literates_person) * 100.0 / NULLIF(SUM(e.total_person), 0)) AS urban_literacy FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Urban' GROUP BY r.area_name ORDER BY urban_literacy DESC LIMIT 1;

