### SQL
SELECT r.area_name, ABS((SUM(e.literates_male) * 100.0 / NULLIF(SUM(e.total_male), 0)) - (SUM(e.literates_female) * 100.0 / NULLIF(SUM(e.total_female), 0))) AS gender_gap FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY gender_gap DESC LIMIT 1;

