### SQL
SELECT ((SUM(e.main_working_female) + SUM(e.marginal_worker_female)) * 100.0 / NULLIF(SUM(e.total_female), 0)) FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE r.area_name = 'Maharashtra' AND t.name = 'Total';

