### SQL
SELECT ((SUM(e.main_working_male) + SUM(e.marginal_worker_male)) * 100.0 / NULLIF(SUM(e.total_male), 0)) FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE r.area_name = 'Uttar Pradesh' AND t.name = 'Total';

