### SQL
SELECT r.area_name, (SUM(e.literates_female) * 100.0 / NULLIF(SUM(e.total_female), 0)) AS urban_female_lit FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Urban' GROUP BY r.area_name ORDER BY urban_female_lit DESC LIMIT 1;

