### SQL
SELECT r.area_name, (SUM(e.literates_male) * 100.0 / NULLIF(SUM(e.total_male), 0)) AS rural_male_lit FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Rural' GROUP BY r.area_name ORDER BY rural_male_lit DESC LIMIT 1;

