### SQL
SELECT r.area_name, SUM(e.main_working_female + e.marginal_worker_female) AS female_workers FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY female_workers DESC LIMIT 1;

