### SQL
SELECT r.area_name, SUM(e.main_working_male + e.marginal_worker_male) AS male_workers FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY male_workers DESC LIMIT 1;

