### SQL
SELECT ((SUM(e.main_working_person) + SUM(e.marginal_worker_person)) * 100.0 / NULLIF(SUM(e.total_person), 0)) FROM education_stats e JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Urban';

