### SQL
SELECT r.area_name, (SUM(e.literates_male) * 100.0 / NULLIF(SUM(e.total_male), 0)) AS lit_rate FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY lit_rate ASC LIMIT 1;

