### SQL
SELECT r.area_name, SUM(e.main_working_person) AS rural_main_workers FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Rural' GROUP BY r.area_name ORDER BY rural_main_workers DESC LIMIT 1;

