### SQL
SELECT r.area_name, SUM(e.non_working_person) AS urban_non_workers FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Urban' GROUP BY r.area_name ORDER BY urban_non_workers DESC LIMIT 1;

