### SQL
SELECT r.area_name, (SUM(e.literates_female) * 1.0 / NULLIF(SUM(e.literates_male), 0)) AS ratio FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY ratio ASC LIMIT 1;

