### SQL
SELECT ABS((SUM(CASE WHEN t.name = 'Urban' THEN e.literates_person ELSE 0 END) * 100.0 / NULLIF(SUM(CASE WHEN t.name = 'Urban' THEN e.total_person ELSE 0 END), 0)) - (SUM(CASE WHEN t.name = 'Rural' THEN e.literates_person ELSE 0 END) * 100.0 / NULLIF(SUM(CASE WHEN t.name = 'Rural' THEN e.total_person ELSE 0 END), 0))) FROM education_stats e JOIN tru t ON e.tru_id = t.id;

