### SQL
SELECT r.area_name, SUM(e.non_working_female) AS female_non_workers FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY female_non_workers DESC LIMIT 1;

