### SQL
SELECT r.area_name, SUM(e.non_working_male) AS male_non_workers FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY male_non_workers DESC LIMIT 1;

