### SQL
SELECT AVG(main_workers) FROM (SELECT SUM(main_working_person) as main_workers FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name) sub;

