### SQL
SELECT AVG(marginal_workers) FROM (SELECT SUM(marginal_worker_person) as marginal_workers FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name) sub;

