### SQL
SELECT r.area_name, (SUM(e.marginal_worker_person) * 100.0 / NULLIF(SUM(e.main_working_person + e.marginal_worker_person), 0)) AS marginal_ratio FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY marginal_ratio DESC LIMIT 1;

