### SQL
SELECT r.area_name, (SUM(e.non_working_person) * 100.0 / NULLIF(SUM(e.total_person), 0)) AS non_work_rate FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY non_work_rate ASC LIMIT 1;

