### SQL
SELECT SUM(e.main_working_person + e.marginal_worker_person) FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE r.area_name = 'Karnataka' AND t.name = 'Urban';

