### SQL
SELECT r.area_name, SUM(e.literates_person) AS rural_literates FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Rural' GROUP BY r.area_name ORDER BY rural_literates DESC LIMIT 1;

