### SQL
SELECT r.area_name, SUM(e.literates_person) AS urban_literates FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Urban' GROUP BY r.area_name ORDER BY urban_literates ASC LIMIT 1;

