### SQL
SELECT r.area_name, SUM(e.main_working_female) AS female_main_workers FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY female_main_workers DESC LIMIT 1;

