### SQL
SELECT (SUM(e.literates_person) * 100.0 / NULLIF(SUM(e.total_person), 0)) FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' AND r.state = (SELECT state FROM population_stats WHERE tru_id = (SELECT id FROM tru WHERE name = 'Total') ORDER BY persons DESC LIMIT 1);

