### SQL
SELECT r.area_name, (SUM(e.total_worker_female) * 100.0 / NULLIF(SUM(e.total_worker_person), 0)) AS female_pct FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY female_pct DESC LIMIT 1;

