### SQL
SELECT r.area_name, SUM(e.illiterate_persons) AS ill_workers FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY ill_workers DESC LIMIT 1;

