### SQL
SELECT (SUM(main_cultivator_person + marginal_cultivator_person) * 1.0 / NULLIF(SUM(main_agricultural_labourers_person + marginal_agriculture_labourers_person), 0)) FROM education_stats WHERE tru_id = (SELECT id FROM tru WHERE name = 'Total');

