### SQL
SELECT r.area_name, (SUM(e.main_working_person) * 1.0 / NULLIF(SUM(e.marginal_worker_person), 0)) AS ratio FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY ratio ASC LIMIT 1;

