### SQL
SELECT r.area_name, SUM(e.non_working_female) AS urban_fem_nonwork FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Urban' GROUP BY r.area_name ORDER BY urban_fem_nonwork DESC LIMIT 1;

