### SQL
SELECT r.area_name, SUM(e.non_working_male) AS rural_male_nonwork FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Rural' GROUP BY r.area_name ORDER BY rural_male_nonwork DESC LIMIT 1;

