### SQL
SELECT r.area_name, SUM(e.marginal_worker_population_3_6_person) AS val FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY val DESC LIMIT 1;

