### SQL
SELECT r.area_name, (SUM(e.main_agricultural_labourers_person + e.marginal_agriculture_labourers_person) * 100.0 / NULLIF(SUM(e.total_worker_person), 0)) AS pct FROM education_stats e JOIN regions r ON e.state = r.state JOIN tru t ON e.tru_id = t.id WHERE t.name = 'Total' GROUP BY r.area_name ORDER BY pct DESC LIMIT 1;

